import tensorflow as tf
import keras

X = tf.constant([[0, 0], [0, 1], [1, 0], [1, 1]], name="GATE_INPUT")
y = tf.constant([0, 1, 1, 0], name="OUTPUT")

model = keras.Sequential(layers=[], name="XOR_GATE")    # 자주 쓰이는 모델 적층형태, funcrional API 방식, class 방식
input_layer = keras.layers.Input(shape=(2,), name="Input_Layer")
model.add(layer=input_layer)
layer1 = keras.layers.Dense(units=10, activation="relu", name="Layer1")   # keras.layers.Dense 외워라
model.add(layer=layer1)
layer2 = keras.layers.Dense(units=16, activation="relu", name="Layer2")   # keras.layers.Dense 외워라
model.add(layer=layer2)
layer3 = keras.layers.Dense(units=8, activation="relu", name="Layer3")   # keras.layers.Dense 외워라
model.add(layer=layer3)
output_layer = keras.layers.Dense(units=1, activation='sigmoid', name="Output_Layer")
model.add(layer=output_layer)

model.compile(optimizer='adam', loss='binary_crossentropy', metrics=['accuracy'])

model.summary()

Model = model.fit(x=X, y=y, epochs=1000, verbose=2)
print(Model.history['accuracy'])
print(model.predict(x=X))

